App.Init:

			function SetDefaults(SubMenu) {
        const cheats = [];
        cheats.push("480p");
        cheats.push("SMOOTH");
        cheats.push("NOIGR", "IGR0", "IGR1", "IGR2", "IGR3", "IGR4", "IGR5");
        cheats.push("HDTVFIX");
        cheats.push("WIDESCREEN", "ULTRA_WIDESCREEN", "EYEFINITY");
        cheats.push("SCANLINES");
		    const dev = (os.readdir("mass:/")[0].includes("POPS")) ? "mass" : "hdd";
		    const statuses = getPOPSCheat({ cheats: cheats, device: dev});
				for (let j = 0; j < 6; j++)	{
          let item = SubMenu.Items[j + 3];
          let defval = 0;
          switch(j) {
            case 0:
            case 1: defval = Number(statuses[j]); break;
            case 2:
              defval = (statuses[2]) ? 7 : 0;
              defval = (statuses[3]) ? 1 : defval;
              defval = (statuses[4]) ? 2 : defval;
              defval = (statuses[5]) ? 3 : defval;
              defval = (statuses[6]) ? 4 : defval;
              defval = (statuses[7]) ? 5 : defval;
              defval = (statuses[8]) ? 6 : defval;
              break;
            case 3: defval = Number(statuses[9]); break;
            case 4:
              defval = (statuses[10]) ? 1 : 0;
              defval = (statuses[11]) ? 2 : defval;
              defval = (statuses[12]) ? 3 : defval;
              break;
            case 5: defval = Number(statuses[13]); break;
          }

          item.Value.Default = defval;
				}

        delete SubMenu.Init;
			}

Option.Confirm:

function(index, item) {
  let cheats = []; cheats.push({ code: "480p", enabled: (index === 1) });
  setPOPSCheat({ cheats: cheats });
  if (UserConfig.HDD) setPOPSCheat({ cheats: cheats, device:"hdd" });
}


function(index, item) {
  let cheats = []; cheats.push({ code: "SMOOTH", enabled: (index === 1) });
  setPOPSCheat({ cheats: cheats });
  if (UserConfig.HDD) setPOPSCheat({ cheats: cheats, device:"hdd" });
}


function(index, item) {
  const cheats = [];
  cheats.push({ code: "IGR0", enabled: (index === 1) });
  cheats.push({ code: "IGR1", enabled: (index === 2) });
  cheats.push({ code: "IGR2", enabled: (index === 3) });
  cheats.push({ code: "IGR3", enabled: (index === 4) });
  cheats.push({ code: "IGR4", enabled: (index === 5) });
  cheats.push({ code: "IGR5", enabled: (index === 6) });
  cheats.push({ code: "NOIGR", enabled: (index === 7) });
  setPOPSCheat({ cheats: cheats });
  if (UserConfig.HDD) setPOPSCheat({ cheats: cheats, device:"hdd" });
}


